wind <- wind_raw %>%
  mutate(Start = tsibble::yearmonth(Start),
         End = tsibble::yearmonth(End)) %>%
  filter(lubridate::year(Start) < 2019,
         lubridate::year(End) >= 2020)


metar <- metar_raw %>%
  mutate(Start = tsibble::yearmonth(Start),
         End = tsibble::yearmonth(End)) %>%
  filter(lubridate::year(Start) < 2019,
         lubridate::year(End) >= 2020)

library(cubble)
vic_map <- ozmaps::abs_ste %>% filter(NAME == "Victoria")
wind_meta <- weatherdata::wind %>%
  as_tibble() %>%
  select(-ts)

wind_data <- weatherdata::wind %>%
  stretch() %>%
  filter(between(lubridate::date(time), as.Date("2019-10-01"), as.Date("2020-03-31")),
         direction <= 360) %>%
  as_tibble()

plot_map(vic_map) +
  #geom_point(data = wind_meta, aes(x = lon, y = lat), color = "red") +
  geom_point(data = metar, aes(x = Lon, y = Lat), color = "red") +
  geom_point(data = wind_meta, aes(x = lon, y = lat), size = 0.8)
